var io = require('socket.io')(80);

io.on('connection', function (socket) {
  console.log('fjdfj');
  socket.on('message', function (msg) {
	  console.log(msg);
	  socket.emit('r', 'i have reseve');
  });
  socket.emit('e', 'welcome!');
  socket.on('disconnect', function () {
	  console.log('disconnect');
  });
});
